-- tblSites
CREATE TABLE IF NOT EXISTS tblSites (
    ID SERIAL PRIMARY KEY,
    Name TEXT,
    LocationDescription TEXT
);

-- tblLocations
CREATE TABLE IF NOT EXISTS tblLocations (
    SiteID INTEGER PRIMARY KEY,
    Name TEXT NOT NULL,
    Latitude REAL,
    Longitude REAL
);

-- tblSiteOwners
CREATE TABLE IF NOT EXISTS tblSiteOwners (
    SiteID INTEGER PRIMARY KEY,
    OwnerName TEXT NOT NULL,
    OwnerEmail TEXT,
    OwnerPhone TEXT,
    Notes TEXT
);

-- tblUsers
CREATE TABLE IF NOT EXISTS tblUsers (
    ID SERIAL PRIMARY KEY,
    Username TEXT NOT NULL UNIQUE,
    Password TEXT NOT NULL,
    IsActive INTEGER NOT NULL DEFAULT 1,
    CreatedAt TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP
);

-- tblJobs
CREATE TABLE IF NOT EXISTS tblJobs (
    ID SERIAL PRIMARY KEY,
    SiteID INTEGER NOT NULL,
    FLKDesc TEXT,
    FLK TEXT,
    LocationDesc TEXT,
    PlantDesc TEXT,
    WorkCenterDesc TEXT,
    EAgI TEXT,
    EAgIDesc TEXT,
    TaskDesc TEXT,
    CycleDaysRCM INTEGER,
    Frequency TEXT,
    NextCallDate DATE,
    DueDate DATE,
    DaysOverdue INTEGER,
    Reason TEXT,
    FacilityKeyDesc TEXT,
    FOREIGN KEY(SiteID) REFERENCES tblSites(ID) ON DELETE CASCADE
);

